// Listen for content block start to detect thinking blocks
stream.on('streamEvent', (event) => {
  if (event.type === 'content_block_start') {
    if (event.content_block.type === 'thinking') {
      // Show thinking indicator
      console.log(`\n${pc.magenta('ðŸ’­')} ${pc.dim('Thinking')} ${pc.dim('â€º')}`)
      isThinkingBlock = true
    } else if (event.content_block.type === 'text') {
      // Show response indicator
      console.log(`\n${pc.green('Claude')} ${pc.dim('â€º')}`)
      isThinkingBlock = false
    }
  } else if (event.type === 'content_block_delta') {
    if (event.delta.type === 'text_delta') {
      // Apply different styling for thinking vs response
      const text = event.delta.text
      if (isThinkingBlock) {
        process.stdout.write(pc.dim(pc.magenta(text)))
      } else {
        process.stdout.write(text)
      }
    }
  }
})

// Store thinking blocks for later expansion
const thinkingBlocks: Array<{id: string, content: string, tokens: number}> = []

stream.on('streamEvent', (event) => {
  if (event.type === 'content_block_start' && event.content_block.type === 'thinking') {
    currentThinkingBlock = {
      id: event.content_block.id,
      content: '',
      tokens: 0
    }
    // Show collapsed indicator
    console.log(`\n${pc.dim(pc.magenta('ðŸ’­ [Thinking...] '))}`)
  } else if (event.type === 'content_block_delta' && currentThinkingBlock) {
    // Accumulate thinking content but don't display
    currentThinkingBlock.content += event.delta.text
    currentThinkingBlock.tokens += 1
  } else if (event.type === 'content_block_stop' && currentThinkingBlock) {
    // Show summary with expand option
    const summary = currentThinkingBlock.content.slice(0, 60) + '...'
    console.log(pc.dim(`ðŸ’­ ${summary} (${currentThinkingBlock.tokens} tokens)`))
    console.log(pc.cyan('   Press [t] to expand thinking'))
    thinkingBlocks.push(currentThinkingBlock)
    currentThinkingBlock = null
  }
})

// Handle keyboard input to expand
process.stdin.on('keypress', (str, key) => {
  if (key.name === 't') {
    // Show full thinking blocks
    thinkingBlocks.forEach(block => {
      console.log(pc.magenta('\nðŸ’­ Extended Thinking:'))
      console.log(pc.dim(block.content))
      console.log(pc.dim(`(${block.tokens} tokens used)`))
    })
  }
})

enum CognitiveState {
  Analyzing = 'analyzing',
  Thinking = 'thinking',
  Deciding = 'deciding',
  Executing = 'executing',
  Responding = 'responding'
}

const stateConfig = {
  analyzing: { icon: 'ðŸ”', color: pc.cyan, label: 'Analyzing' },
  thinking: { icon: 'ðŸ’­', color: pc.magenta, label: 'Thinking' },
  deciding: { icon: 'âš–ï¸', color: pc.yellow, label: 'Deciding' },
  executing: { icon: 'âš¡', color: pc.yellow, label: 'Executing' },
  responding: { icon: 'ðŸ’¬', color: pc.green, label: 'Responding' }
}

let currentState: CognitiveState | null = null

function showState(state: CognitiveState) {
  if (currentState === state) return
  currentState = state
  const config = stateConfig[state]
  console.log(`\n${config.icon} ${config.color(config.label)} ${pc.dim('â€º')}`)
}

stream.on('streamEvent', (event) => {
  if (event.type === 'content_block_start') {
    switch (event.content_block.type) {
      case 'thinking':
        showState(CognitiveState.Thinking)
        break
      case 'tool_use':
        showState(CognitiveState.Deciding)
        break
      case 'text':
        showState(CognitiveState.Responding)
        break
    }
  }
})

// During tool execution
showState(CognitiveState.Executing)
await executeTool(toolName, toolInput)
showState(CognitiveState.Analyzing) // After tool result received

interface ThinkingBudget {
  total: number
  used: number
  remaining: number
}

function showBudgetProgress(budget: ThinkingBudget) {
  const percentage = (budget.used / budget.total) * 100
  const barLength = 30
  const filled = Math.round((percentage / 100) * barLength)
  const empty = barLength - filled

  // Color-code based on usage
  let color = pc.green
  if (percentage > 80) color = pc.red
  else if (percentage > 60) color = pc.yellow

  const bar = color('â–ˆ'.repeat(filled)) + pc.dim('â–‘'.repeat(empty))
  const label = `${budget.used.toLocaleString()}/${budget.total.toLocaleString()} tokens`

  console.log(`\n${pc.dim('Budget:')} ${bar} ${color(label)} (${percentage.toFixed(1)}%)`)
}

// Track token usage during thinking
let thinkingTokens = 0
const budget = { total: 10000, used: 0, remaining: 10000 }

stream.on('streamEvent', (event) => {
  if (event.type === 'content_block_delta' && event.delta.type === 'text_delta') {
    if (currentlyInThinkingBlock) {
      // Approximate token count (rough estimate)
      thinkingTokens += event.delta.text.split(/\s+/).length
      budget.used = thinkingTokens
      budget.remaining = budget.total - thinkingTokens
    }
  } else if (event.type === 'content_block_stop' && currentlyInThinkingBlock) {
    // Show final budget usage
    showBudgetProgress(budget)
  }
})

// Enable interleaved thinking with beta header
const stream = await client.messages.stream({
  model: 'claude-4-sonnet',
  max_tokens: 4096,
  thinking: {
    type: 'enabled',
    budget_tokens: 10000
  },
  messages: conversation,
  tools: tools
}, {
  headers: {
    'anthropic-beta': 'interleaved-thinking-2025-05-14'
  }
})

let toolCallCount = 0
let thinkingAfterTool = false

stream.on('streamEvent', (event) => {
  if (event.type === 'content_block_start') {
    if (event.content_block.type === 'tool_use') {
      toolCallCount++
      console.log(`\n${pc.yellow('âš¡')} ${pc.bold('Tool Call #' + toolCallCount)}: ${event.content_block.name}`)
      thinkingAfterTool = false
    } else if (event.content_block.type === 'thinking') {
      if (toolCallCount > 0 && !thinkingAfterTool) {
        // This is thinking AFTER a tool result
        console.log(`\n${pc.magenta('ðŸ’­')} ${pc.dim('Reasoning about results...')})`)
        thinkingAfterTool = true
      } else {
        // Initial thinking before tool calls
        console.log(`\n${pc.magenta('ðŸ’­')} ${pc.dim('Planning approach...')})`)
      }
    }
  }
})

// After tool execution, show we're waiting for reasoning
console.log(pc.dim('   Waiting for Claude to analyze results...'))

stream.on('contentBlock', (block) => {
