    console.log(pc.dim('   (Claude still has context internally)'))
  } else if (block.type === 'thinking') {
    // Normal thinking display
    console.log(`\n${pc.magenta('ðŸ’­ Thinking:')}`)
    console.log(pc.dim(block.thinking))
  }
})
